import base64   # 内置模块


# 复制下载的字符串形式的图片
img_str = """"""


"""把字符串形式的图片转换成二进制"""
# base64.b64decode()   括号内部传字符串图片
img_bytes = base64.b64decode(img_str)
# print(img_bytes)

# with open('yzm.png', mode='wb') as f:
#     f.write(img_bytes)


"""把二进制形式的图片转换成字符串"""
with open('yzm.png', mode='rb') as f:
    img_bytes_data = f.read()  # 读取二进制形式的图片
    print(img_bytes_data)

# b64encode()  把二进制形式的图片转换成字符串
img_str = base64.b64encode(img_bytes_data).decode()
print(img_str)

